"use client"

import { useEffect, useState } from "react"
import { Link } from "react-router-dom"
import ReviewCard from "../components/ReviewCard"
import LoadingSpinner from "../components/LoadingSpinner"
import ErrorMessage from "../components/ErrorMessage"
import "./ProfilePage.css"

const API_BASE_URL = process.env.NODE_ENV === "production" ? "/api" : "http://localhost:5000/api"

function ProfilePage() {
  const [user, setUser] = useState(null)
  const [userReviews, setUserReviews] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [isEditing, setIsEditing] = useState(false)
  const [editForm, setEditForm] = useState({
    name: "",
    email: "",
    favorite_genres: [],
  })

  // Mock current user ID - in a real app, this would come from authentication
  const currentUserId = "1"

  useEffect(() => {
    const fetchUserProfile = async () => {
      setLoading(true)
      setError(null)

      try {
        // Fetch user profile
        const userResponse = await fetch(`${API_BASE_URL}/users/${currentUserId}`)
        if (!userResponse.ok) throw new Error("Failed to fetch user profile")
        const userData = await userResponse.json()
        setUser(userData.data)
        setEditForm({
          name: userData.data.name,
          email: userData.data.email,
          favorite_genres: userData.data.favorite_genres || [],
        })

        // Fetch user's reviews
        const reviewsResponse = await fetch(`${API_BASE_URL}/reviews?userId=${currentUserId}`)
        if (!reviewsResponse.ok) throw new Error("Failed to fetch user reviews")
        const reviewsData = await reviewsResponse.json()
        setUserReviews(reviewsData.data)
      } catch (error) {
        setError(error.message || "Failed to load profile")
      } finally {
        setLoading(false)
      }
    }

    fetchUserProfile()
  }, [currentUserId])

  const handleSaveProfile = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/users/${currentUserId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(editForm),
      })

      if (!response.ok) throw new Error("Failed to update profile")

      const updatedUser = await response.json()
      setUser(updatedUser.data)
      setIsEditing(false)
    } catch (error) {
      setError("Failed to update profile")
    }
  }

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
    })
  }

  if (loading) {
    return (
      <div className="page-container">
        <LoadingSpinner size="lg" text="Loading profile..." />
      </div>
    )
  }

  if (error || !user) {
    return (
      <div className="profile-page">
        <div className="container">
          <ErrorMessage message={error || "Profile not found"} />
        </div>
      </div>
    )
  }

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-grid">
          {/* Profile Information */}
          <div className="profile-sidebar">
            <div className="profile-card">
              <div className="profile-header">
                <h2>Profile</h2>
                <button className="edit-button" onClick={() => setIsEditing(!isEditing)}>
                  ‚úèÔ∏è
                </button>
              </div>
              <div className="profile-content">
                <div className="avatar-section">
                  <div className="avatar">{user.name.charAt(0)}</div>

                  {isEditing ? (
                    <div className="edit-form">
                      <div className="form-group">
                        <label htmlFor="name">Name</label>
                        <input
                          id="name"
                          type="text"
                          value={editForm.name}
                          onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                          className="form-input"
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                          id="email"
                          type="email"
                          value={editForm.email}
                          onChange={(e) => setEditForm({ ...editForm, email: e.target.value })}
                          className="form-input"
                        />
                      </div>
                      <div className="form-actions">
                        <button onClick={handleSaveProfile} className="btn btn-primary btn-sm">
                          Save
                        </button>
                        <button onClick={() => setIsEditing(false)} className="btn btn-outline btn-sm">
                          Cancel
                        </button>
                      </div>
                    </div>
                  ) : (
                    <div className="user-info">
                      <h3 className="user-name">{user.name}</h3>
                      <p className="user-email">{user.email}</p>
                    </div>
                  )}
                </div>

                <div className="user-meta">
                  <div className="meta-item">
                    <span className="meta-icon">üìÖ</span>
                    <span>Joined {formatDate(user.joined_date)}</span>
                  </div>
                  <div className="meta-item">
                    <span className="meta-icon">‚≠ê</span>
                    <span>{user.review_count} reviews written</span>
                  </div>
                </div>

                <div className="favorite-genres">
                  <h4>Favorite Genres</h4>
                  <div className="genres-list">
                    {user.favorite_genres &&
                      user.favorite_genres.map((genre) => (
                        <span key={genre} className="genre-tag">
                          {genre}
                        </span>
                      ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Stats Card */}
            <div className="stats-card">
              <h3>Reading Stats</h3>
              <div className="stats-list">
                <div className="stat-item">
                  <span className="stat-label">Reviews Written</span>
                  <span className="stat-value">{user.review_count}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Average Rating Given</span>
                  <span className="stat-value">4.2</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Books Reviewed</span>
                  <span className="stat-value">{userReviews.length}</span>
                </div>
              </div>
            </div>
          </div>

          {/* User Reviews */}
          <div className="reviews-section">
            <div className="reviews-card">
              <div className="reviews-header">
                <h2 className="reviews-title">üìñ My Reviews ({userReviews.length})</h2>
              </div>
              <div className="reviews-content">
                {userReviews.length > 0 ? (
                  <div className="reviews-list">
                    {userReviews.map((review) => (
                      <ReviewCard key={review.id} review={review} />
                    ))}
                  </div>
                ) : (
                  <div className="no-reviews">
                    <p>You haven't written any reviews yet.</p>
                    <Link to="/books" className="btn btn-primary">
                      Browse Books to Review
                    </Link>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default ProfilePage
