"use client"

import { useEffect } from "react"
import { Link } from "react-router-dom"
import { useApp } from "../contexts/AppContext"
import BookCard from "../components/BookCard"
import LoadingSpinner from "../components/LoadingSpinner"
import ErrorMessage from "../components/ErrorMessage"
import "./HomePage.css"

const API_BASE_URL = process.env.NODE_ENV === "production" ? "/api" : "http://localhost:5000/api"

function HomePage() {
  const { state, dispatch } = useApp()

  useEffect(() => {
    const fetchFeaturedBooks = async () => {
      dispatch({ type: "SET_LOADING", payload: true })
      dispatch({ type: "SET_ERROR", payload: null })

      try {
        const response = await fetch(`${API_BASE_URL}/books?featured=true`)
        if (!response.ok) throw new Error("Failed to fetch books")

        const data = await response.json()
        dispatch({ type: "SET_BOOKS", payload: data.data })
      } catch (error) {
        dispatch({ type: "SET_ERROR", payload: "Failed to load featured books" })
      } finally {
        dispatch({ type: "SET_LOADING", payload: false })
      }
    }

    fetchFeaturedBooks()
  }, [dispatch])

  const featuredBooks = state.books.filter((book) => book.featured)

  if (state.loading) {
    return (
      <div className="page-container">
        <LoadingSpinner size="lg" text="Loading featured books..." />
      </div>
    )
  }

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero">
        <div className="container">
          <div className="hero-content">
            <h1 className="hero-title">Discover Your Next Great Read</h1>
            <p className="hero-description">
              Join thousands of readers sharing their thoughts on the books they love. Find your next favorite book
              through authentic reviews and ratings.
            </p>
            <div className="hero-actions">
              <Link to="/books" className="btn btn-primary">
                Browse Books
              </Link>
              <Link to="/reviews/new" className="btn btn-secondary">
                Write a Review
              </Link>
            </div>
          </div>
        </div>
      </section>

      <div className="container">
        {state.error && (
          <div className="error-container">
            <ErrorMessage message={state.error} />
          </div>
        )}

        {/* Featured Books Section */}
        <section className="featured-section">
          <div className="section-header">
            <h2 className="section-title">Featured Books</h2>
            <Link to="/books" className="btn btn-outline">
              View All Books
            </Link>
          </div>

          {featuredBooks.length > 0 ? (
            <div className="books-grid">
              {featuredBooks.map((book) => (
                <BookCard key={book.id} book={book} featured />
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <p>No featured books available at the moment.</p>
            </div>
          )}
        </section>

        {/* Stats Section */}
        <section className="stats-section">
          <div className="stats-grid">
            <div className="stat-card">
              <h3 className="stat-number">10,000+</h3>
              <p className="stat-label">Books in our collection</p>
            </div>
            <div className="stat-card">
              <h3 className="stat-number">50,000+</h3>
              <p className="stat-label">Reviews written</p>
            </div>
            <div className="stat-card">
              <h3 className="stat-number">5,000+</h3>
              <p className="stat-label">Active readers</p>
            </div>
          </div>
        </section>
      </div>
    </div>
  )
}

export default HomePage
